export interface CaptureDetailResponse {
  data: Capture | null;
  meta: { request_id: string; timestamp: string };
}

export interface VerifyResponse {
  data: {
    status: 'verified' | 'c2pa_only' | 'no_record';
    capture_id?: string;
    confidence_level?: ConfidenceLevel;
    verification_url?: string;
  };
  meta: { request_id: string; timestamp: string };
}

